// –ò–°–ü–†–ê–í–õ–ï–ù–ò–ï: –î–æ–±–∞–≤–ª–µ–Ω–∞ –ø—Ä–æ–≤–µ—Ä–∫–∞ –≤—Ö–æ–¥–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö
const input = $input.item.json;
const messageText = input.messageText || input.text || input.message || '';
const cleanText = messageText.trim();

console.log('–í—Ö–æ–¥—è—â–µ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ:', messageText);

// –ò–°–ü–†–ê–í–õ–ï–ù–ò–ï: –ë–æ–ª–µ–µ –Ω–∞–¥–µ–∂–Ω–æ–µ –∏–∑–≤–ª–µ—á–µ–Ω–∏–µ –ø–æ–∏—Å–∫–æ–≤–æ–≥–æ –∑–∞–ø—Ä–æ—Å–∞
let searchTerm = '';

if (cleanText === 'üìã —Ä–µ—Ü–µ–ø—Ç—ã' || cleanText.toLowerCase() === '—Ä–µ—Ü–µ–ø—Ç—ã') {
  // –ï—Å–ª–∏ –ø—Ä–æ—Å—Ç–æ –Ω–∞–∂–∞–ª–∏ –∫–Ω–æ–ø–∫—É "–†–µ—Ü–µ–ø—Ç—ã", –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –≤—Å–µ
  searchTerm = '';
} else {
  // –£–¥–∞–ª—è–µ–º –∫–æ–º–∞–Ω–¥—É –∏ –∏–∑–≤–ª–µ–∫–∞–µ–º –∑–∞–ø—Ä–æ—Å
  searchTerm = cleanText
    .replace(/^\/—Ä–µ—Ü–µ–ø—Ç\s*/i, '')
    .replace(/^\/recipe\s*/i, '')
    .replace(/^—Ä–µ—Ü–µ–ø—Ç\s*/i, '')
    .replace(/^recipe\s*/i, '')
    .trim();
}

if (!searchTerm) {
  return [{ 
    show_all: true,
    message: 'üìã *–î–æ—Å—Ç—É–ø–Ω—ã–µ —Ä–µ—Ü–µ–ø—Ç—ã*\n\n–í–≤–µ–¥–∏—Ç–µ –Ω–∞–∑–≤–∞–Ω–∏–µ —Ä–µ—Ü–µ–ø—Ç–∞ –¥–ª—è –ø–æ–∏—Å–∫–∞ –∏–ª–∏ –ø—Ä–æ—Å–º–æ—Ç—Ä–∏—Ç–µ –≤—Å–µ –¥–æ—Å—Ç—É–ø–Ω—ã–µ —Ä–µ—Ü–µ–ø—Ç—ã –Ω–∏–∂–µ.\n\nüí° *–ü—Ä–∏–º–µ—Ä:* `/—Ä–µ—Ü–µ–ø—Ç D500` –∏–ª–∏ `/—Ä–µ—Ü–µ–ø—Ç –±–ª–æ–∫`'
  }];
}

console.log(`–ü–æ–∏—Å–∫ —Ä–µ—Ü–µ–ø—Ç–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É: "${searchTerm}"`);
return [{ 
  searchTerm: searchTerm,
  show_all: false 
}];

// –ò–°–ü–†–ê–í–õ–ï–ù–ò–ï: –ü—Ä–∞–≤–∏–ª—å–Ω–∞—è –æ–±—Ä–∞–±–æ—Ç–∫–∞ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤ MySQL
const inputItems = $input.all();
let recipes = [];

// –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –≤—Å–µ –≤—Ö–æ–¥—è—â–∏–µ —ç–ª–µ–º–µ–Ω—Ç—ã
for (const item of inputItems) {
  if (item.json && typeof item.json === 'object') {
    // –ï—Å–ª–∏ —ç—Ç–æ –º–∞—Å—Å–∏–≤ —Ä–µ—Ü–µ–ø—Ç–æ–≤
    if (Array.isArray(item.json)) {
      recipes = recipes.concat(item.json);
    } 
    // –ï—Å–ª–∏ —ç—Ç–æ –æ—Ç–¥–µ–ª—å–Ω—ã–π —Ä–µ—Ü–µ–ø—Ç
    else if (item.json.recipe_id) {
      recipes.push(item.json);
    }
  }
}

console.log('–ù–∞–π–¥–µ–Ω–Ω—ã–µ —Ä–µ—Ü–µ–ø—Ç—ã:', JSON.stringify(recipes, null, 2));

if (!recipes || recipes.length === 0) {
  return [{ 
    error: true,
    message: 'üîç *–†–µ—Ü–µ–ø—Ç—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã*\n\n–ü–æ –≤–∞—à–µ–º—É –∑–∞–ø—Ä–æ—Å—É –Ω–∏—á–µ–≥–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ.\n\nüí° *–ü–æ–ø—Ä–æ–±—É–π—Ç–µ:*\n‚Ä¢ –ü—Ä–æ–≤–µ—Ä–∏—Ç—å –ø—Ä–∞–≤–∏–ª—å–Ω–æ—Å—Ç—å –Ω–∞–ø–∏—Å–∞–Ω–∏—è\n‚Ä¢ –ò—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å —á–∞—Å—Ç—å –Ω–∞–∑–≤–∞–Ω–∏—è\n‚Ä¢ –ò—Å–∫–∞—Ç—å –ø–æ —Ç–∏–ø—É –ø—Ä–æ–¥—É–∫—Ç–∞ (–±–ª–æ–∫, –ø–∞–Ω–µ–ª—å)\n\nüìã –î–ª—è –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ –≤—Å–µ—Ö —Ä–µ—Ü–µ–ø—Ç–æ–≤ –æ—Ç–ø—Ä–∞–≤—å—Ç–µ `/—Ä–µ—Ü–µ–ø—Ç`'
  }];
}

let message = 'üìã *–ù–∞–π–¥–µ–Ω–Ω—ã–µ —Ä–µ—Ü–µ–ø—Ç—ã:*\n\n';

for (let i = 0; i < recipes.length && i < 5; i++) {
  const recipe = recipes[i];

  message += `üèóÔ∏è **${recipe.recipe_name || '–ë–µ–∑ –Ω–∞–∑–≤–∞–Ω–∏—è'}**\n`;

  if (recipe.product_code) {
    message += `üìã –ö–æ–¥: ${recipe.product_code}\n`;
  }

  if (recipe.density_kg_m3) {
    message += `‚öñÔ∏è –ü–ª–æ—Ç–Ω–æ—Å—Ç—å: ${recipe.density_kg_m3} –∫–≥/–º¬≥\n`;
  }

  message += `\n*–°–æ—Å—Ç–∞–≤ –Ω–∞ 1 –º¬≥:*\n`;
  message += `‚Ä¢ üèóÔ∏è –¶–µ–º–µ–Ω—Ç: ${recipe.cement_kg || 0} –∫–≥\n`;
  message += `‚Ä¢ üèîÔ∏è –ò–∑–≤–µ—Å—Ç—å: ${recipe.lime_kg || 0} –∫–≥\n`;
  message += `‚Ä¢ üèñÔ∏è –ü–µ—Å–æ–∫: ${recipe.sand_kg || 0} –∫–≥\n`;
  message += `‚Ä¢ üíß –í–æ–¥–∞: ${recipe.water_liters || 0} –ª\n`;
  message += `‚Ä¢ ‚ú® –ê–ª—é–º–∏–Ω–∏–π: ${recipe.aluminum_powder_kg || 0} –∫–≥\n`;

  if (recipe.mixing_time_minutes) {
    message += `‚è±Ô∏è –í—Ä–µ–º—è —Å–º–µ—à–∏–≤–∞–Ω–∏—è: ${recipe.mixing_time_minutes} –º–∏–Ω\n`;
  }

  if (recipe.strength_mpa) {
    message += `üí™ –ü—Ä–æ—á–Ω–æ—Å—Ç—å: ${recipe.strength_mpa} –ú–ü–∞\n`;
  }

  message += `‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ\n`;
}

if (recipes.length > 5) {
  message += `\nüìå –ü–æ–∫–∞–∑–∞–Ω–æ –ø–µ—Ä–≤—ã—Ö 5 –∏–∑ ${recipes.length} –Ω–∞–π–¥–µ–Ω–Ω—ã—Ö —Ä–µ—Ü–µ–ø—Ç–æ–≤.\n–£—Ç–æ—á–Ω–∏—Ç–µ –∑–∞–ø—Ä–æ—Å –¥–ª—è –±–æ–ª–µ–µ —Ç–æ—á–Ω–æ–≥–æ –ø–æ–∏—Å–∫–∞.`;
}

return [{ 
  error: false,
  message: message 
}];
